  function redirecionarComUTMs() {
    var botoes = document.getElementsByTagName('a');

    for (var i = 0; i < botoes.length; i++) {
      botoes[i].addEventListener('click', function(e) {
        e.preventDefault(); // Impede o comportamento padrão de clicar no link

        var url = this.getAttribute('href');
        var utms = getUTMs();

        // Verifica se o URL já possui parâmetros
        if (url.indexOf('?') !== -1) {
          url += '&' + utms;
        } else {
          url += '?' + utms;
        }

        // Redireciona para o URL completo
        window.location.href = url;
      });
    }
  }

  function getUTMs() {
    var utms = '';
    var parametros = ['utm_source', 'utm_medium', 'utm_campaign', 'utm_term', 'utm_content'];

    // Obtém os valores dos parâmetros de URL da página atual
    for (var i = 0; i < parametros.length; i++) {
      var valor = getURLParameter(parametros[i]);
      if (valor) {
        if (utms !== '') {
          utms += '&';
        }
        utms += parametros[i] + '=' + valor;
      }
    }

    return utms;
  }

  function getURLParameter(name) {
    name = name.replace(/[\[\]]/g, '\\$&');
    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)');
    var results = regex.exec(window.location.href);

    if (!results) {
      return null;
    }

    if (!results[2]) {
      return '';
    }

    return decodeURIComponent(results[2].replace(/\+/g, ' '));
  }

